    public void changeInformation(int playerLocate, bool havemoney)
    {
        if(havemoney) buyButton.GetComponent<Button>().interactable = true;
        else buyButton.GetComponent<Button>().interactable = false;
        informationTitle.GetComponent<Text>().text = title[playerLocate];
        informationText.GetComponent<Text>().text = text[playerLocate];
        informationImage.GetComponent<Image>().sprite = Image01[playerLocate];
        buyButtonText.GetComponent<Text>().text = buyinformation[playerLocate];
        mapui.SetActive(true);
    }
